import React, { useState, useEffect } from 'react';

const EventDetails = ({ events }) => {
    const [currentDate, setCurrentDate] = useState(null);

    // Helper function to format the date to YYYY-MM-DD
    const formatDate = (date) => {
        return date.toISOString().split('T')[0];
    };

    // Filter events for the current date in YYYY-MM-DD format
    const filteredEvents = events.filter(event =>
        formatDate(new Date(event.start)) === currentDate
    );

    const handlePrev = () => {
        const prevDate = new Date(currentDate);
        prevDate.setDate(prevDate.getDate() - 1);
        setCurrentDate(formatDate(prevDate));
    };

    const handleNext = () => {
        const nextDate = new Date(currentDate);
        nextDate.setDate(nextDate.getDate() + 1);
        setCurrentDate(formatDate(nextDate));
    };

    // Set initial date to the first event's date or today if no events
    useEffect(() => {
        if (events.length > 0) {
            setCurrentDate(formatDate(new Date(events[0].start)));
        } else {
            setCurrentDate(formatDate(new Date()));
        }
    }, [events]);

    return (
        <div>
            <h2>Event Details</h2>
            <div id="event-details" style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '10px', backgroundColor: '#ffffff', height: '300px', overflowY: 'auto', marginBottom: '20px' }}>
                {filteredEvents.length > 0 ? (
                    filteredEvents.map((event, index) => (
                        <div key={index}>
                            <h3>{event.title}</h3>
                            <p>Date Start: {new Date(event.start).toLocaleDateString()}</p>
                            <p>Date End: {event.end ? new Date(event.end).toLocaleDateString() : 'N/A'}</p>
                            <p>Location: {event.extendedProps?.location || 'N/A'}</p>
                            <p>Description: {event.extendedProps?.description || 'N/A'}</p>
                            <hr />
                        </div>
                    ))
                ) : (
                    <p>No events for this date.</p>
                )}
            </div>
            <button className="btn btn-primary" onClick={handlePrev}>Previous Day</button>
            <button className="btn btn-primary" onClick={handleNext}>Next Day</button>
        </div>
    );
};

export default EventDetails;
