// src/App.js
import React, { useState } from 'react';
import NavigationBar from './components/NavigationBar';
import LoginModal from './components/LoginModal';
import SignupModal from './components/SignupModal';
import EventDetails from './components/EventDetails';
import CalendarComponent from './components/CalendarComponent';
import CarouselComponent from './components/CarouselComponent';
import Footer from './components/Footer';
import "./app.css";

const App = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [eventDetails, setEventDetails] = useState('<p>No events on this date.</p>');
  const events = [
    { title: 'Event 1', start: '2024-09-17', end: '2024-09-19', extendedProps: { location: 'Mumbai', description: 'This is a description of Event 1.' } },
    { title: 'Liquor event', start: '2024-09-17', extendedProps: { location: 'Unknown', description: 'Details not provided.' } },
    { title: 'Event 2', start: '2024-09-18', extendedProps: { location: 'Delhi', description: 'This is a description of Event 2.' } },
    { title: 'Event 3', start: '2024-09-20', extendedProps: { location: 'Chennai', description: 'This is a description of Event 3.' } },
    { title: 'Event 4', start: '2024-09-22', extendedProps: { location: 'Bangalore', description: 'This is a description of Event 4.' } },
  ];

  const handleDateClick = (arg) => {
    const selectedEvent = events.find(event => event.start === arg.dateStr);
    setEventDetails(selectedEvent ? 
      `<h4>${selectedEvent.title}</h4><p>${selectedEvent.extendedProps.description}</p>` : 
      '<p>No events on this date.</p>'
    );
  };

  return (
    <div>
      <NavigationBar handleShowLogin={() => setShowLogin(true)} handleShowSignup={() => setShowSignup(true)} />
      <CarouselComponent />
      <div className="container my-4">
        <CalendarComponent events={events} onDateClick={handleDateClick} />
        <EventDetails details={eventDetails} />
      </div>
      <Footer />
      <LoginModal show={showLogin} handleClose={() => setShowLogin(false)} />
      <SignupModal show={showSignup} handleClose={() => setShowSignup(false)} />
    </div>
  );
};

export default App;
