import React, { useState, useEffect } from 'react';

const EventDetails = ({ selectedDate, onDateChange }) => {
    const [events, setEvents] = useState([]);
    const [filteredEvents, setFilteredEvents] = useState([]);

    useEffect(() => {
        // Fetch events from the API
        const fetchEvents = async () => {
            try {
                const response = await fetch('http://localhost:5000/events');
                const data = await response.json();
                setEvents(data);
            } catch (error) {
                console.error('Error fetching events:', error);
            }
        };

        fetchEvents();
    }, []);

    useEffect(() => {
        // Filter events by selected date
        const matchingEvents = events.filter(event => {
            const eventDate = event.start.split('T')[0];
            return eventDate === selectedDate;
        });
        setFilteredEvents(matchingEvents);
    }, [events, selectedDate]);

    const handlePrev = () => {
        const prevDate = new Date(selectedDate);
        prevDate.setDate(prevDate.getDate() - 1);
        onDateChange(prevDate.toISOString().split('T')[0]);
    };

    const handleNext = () => {
        const nextDate = new Date(selectedDate);
        nextDate.setDate(nextDate.getDate() + 1);
        onDateChange(nextDate.toISOString().split('T')[0]);
    };

    return (
        <div>
            <h2>ùúÆùùÇùú∫ùúºùùâ ùë´ùúÆùúØùúüùú§ùë≥ùë∫</h2>
            <div id="event-details" style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '10px', backgroundColor: '#ffffff', height: '300px', overflowY: 'auto', marginBottom: '20px' }}>
                {filteredEvents.length > 0 ? (
                    filteredEvents.map((event, index) => (
                        <div key={index}>
                            <h3>{event.title}</h3>
                            <p>Date Start: {new Date(event.start).toLocaleDateString()}</p>
                            <p>Date End: {event.end ? new Date(event.end).toLocaleDateString() : 'N/A'}</p>
                            <p>Location: {event.extendedProps?.location || 'N/A'}</p>
                            <p>Description: {event.extendedProps?.description || 'N/A'}</p>
                            <hr />
                        </div>
                    ))
                ) : (
                    <p>No events for this date.</p>
                )}
            </div>
            <button className="btn btn-primary" onClick={handlePrev}>Previous Day</button>
            <button className="btn btn-primary" onClick={handleNext}>Next Day</button>
        </div>
    );
};

export default EventDetails;
