import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const SignUpModal = ({ isOpen, onClose }) => {
    const [formData, setFormData] = useState({
        name: '',
        age: '',
        mobile: '',
        email: '',
        experience: '',
        password: ''
    });
    
    const [errors, setErrors] = useState({});

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [id]: value }));
    };

    const validateForm = () => {
        const newErrors = {};
        if (!formData.name) newErrors.name = 'Name is required';
        if (formData.age < 18 || formData.age > 100) newErrors.age = 'Age must be between 18 and 100';
        if (!/^\d{10}$/.test(formData.mobile)) newErrors.mobile = 'Please enter a valid 10-digit mobile number';
        if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Please enter a valid email address';
        if (formData.password.length < 8) newErrors.password = 'Password must be at least 8 characters';
        // Add more validations as needed
        return newErrors;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const validationErrors = validateForm();
        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
        } else {
            console.log('Form submitted with data:', formData);
            // Handle form submission logic here
            onClose(); // Close the modal after successful submission
        }
    };

    return (
        <Modal show={isOpen} onHide={onClose}>
            <Modal.Header closeButton>
                <Modal.Title>Sign Up</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <form id="signupForm" onSubmit={handleSubmit} noValidate>
                    {Object.keys(errors).map((errorKey) => (
                        <div key={errorKey} className="text-danger">{errors[errorKey]}</div>
                    ))}
                    <div className="form-group">
                        <label htmlFor="name">Name</label>
                        <input type="text" className="form-control" id="name" placeholder="Enter name" value={formData.name} onChange={handleChange} required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="age">Age</label>
                        <input type="number" className="form-control" id="age" placeholder="Enter your AGE" value={formData.age} onChange={handleChange} required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="mobile">Mobile no.</label>
                        <input type="tel" className="form-control" id="mobile" placeholder="Whatsapp Preferable" value={formData.mobile} onChange={handleChange} required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Email address</label>
                        <input type="email" className="form-control" id="email" placeholder="Enter email" value={formData.email} onChange={handleChange} required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="experience">Experiences</label>
                        <input type="text" className="form-control" id="experience" placeholder="Write event type" value={formData.experience} onChange={handleChange} required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" id="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
                        <small className="form-text text-muted">Password must be at least 8 characters and contain a combination of uppercase, lowercase, number, and special character.</small>
                    </div>
                    <Button type="submit" variant="primary">Sign Up</Button>
                </form>
            </Modal.Body>
        </Modal>
    );
};

export default SignUpModal;
