import React, { useState, useEffect, useRef } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';

const CalendarSidebar = ({ onDateSelect }) => {
    const calendarRef = useRef(null);
    const [events, setEvents] = useState([]); // State to hold events fetched from MongoDB
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);

    // Fetch events from backend API
    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/events'); // Replace with your actual API endpoint
                const data = await response.json();
                setEvents(data); // Set the fetched events to the state
            } catch (error) {
                console.error("Error fetching events:", error);
            }
        };

        fetchEvents();
    }, []); // Fetch events only once when the component mounts

    // Handles going to today and showing today's events
    const handleToday = () => {
        let calendarApi = calendarRef.current?.getApi();
        if (calendarApi) {
            calendarApi.today();
            const today = new Date().toISOString().split('T')[0];
            setSelectedDate(today);
            onDateSelect(today);  // Trigger event details update for today
        }
    };

    // Handles manual date selection
    const handleDateChange = (e) => {
        const selected = e.target.value;
        setSelectedDate(selected);
        onDateSelect(selected);
    };

    // Handles clicking a date on the calendar
    const handleDateClick = (info) => {
        const clickedDate = info.dateStr;
        setSelectedDate(clickedDate);
        onDateSelect(clickedDate);
    };

    return (
        <div>
            <h4>Calendar</h4>
            <div className="calendar-controls mb-3">
                {/* Today button */}
                <button className="btn btn-secondary" onClick={handleToday}>Today</button>
                
                {/* Manual date input field */}
                <input type="date" className="form-control mt-2" value={selectedDate} onChange={handleDateChange} />
            </div>
            
            {/* FullCalendar instance */}
            <FullCalendar
                ref={calendarRef}
                plugins={[dayGridPlugin, interactionPlugin]}
                initialView="dayGridMonth"
                events={events}  // Events from MongoDB API
                dateClick={handleDateClick}
                headerToolbar={{
                    left: 'prev,next',
                    center: 'title',
                    right: ''
                }}
            />
        </div>
    );
};

export default CalendarSidebar;
