import React, { useState } from 'react';
const events = [
    {
        title: 'Event 1',
        start: '2024-09-17',
        end: '2024-09-19',
        extendedProps: {
            location: 'Mumbai',
            description: 'This is a description of Event 1.',
        },
    },
    {
        title: 'Liquor event',
        start: '2024-09-17',
        extendedProps: {
            location: 'Unknown',
            description: 'Details not provided.',
        },
    },
    {
        title: 'Event 2',
        start: '2024-09-18',
        extendedProps: {
            location: 'Delhi',
            description: 'This is a description of Event 2.',
        },
    },
    {
        title: 'Event 3',
        start: '2024-09-20',
        extendedProps: {
            location: 'Chennai',
            description: 'This is a description of Event 3.',
        },
    },
    {
        title: 'Event 4',
        start: '2024-09-22',
        extendedProps: {
            location: 'Bangalore',
            description: 'This is a description of Event 4.',
        },
    },
    {
        title: 'Event 5',
        start: '2024-09-25',
        extendedProps: {
            location: 'Hyderabad',
            description: 'This is a description of Event 5.',
        },
    },
];

const handleEventClick = (info) => {
    alert(`Event clicked: ${info.event.title}`);
};
// In the parent component, pass the events and handler to CalendarSidebar
<CalendarSidebar events={events} onEventClick={handleEventClick} />

export default events;
