import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EventForm.css';  // Import CSS for styling

const EventForm = () => {
    const [title, setTitle] = useState('');
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [location, setLocation] = useState('');
    const [description, setDescription] = useState('');
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        // Fetch all events when the page loads
        const fetchEvents = async () => {
            try {
                const response = await axios.get('http://localhost:5000/events');
                setEvents(response.data);
            } catch (error) {
                console.error('Failed to fetch events:', error);
            }
        };
        fetchEvents();
    }, []);

    const handleAddEvent = async (e) => {
        e.preventDefault();

        if (!title || !start || !end || !location || !description) {
            setError('Please fill all fields');
            return;
        }

        try {
            const newEvent = { title, start, end, extendedProps: { location, description } };
            await axios.post('http://localhost:5000/events', newEvent, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setEvents([...events, newEvent]);
            setTitle('');
            setStart('');
            setEnd('');
            setLocation('');
            setDescription('');
        } catch (err) {
            setError('Failed to add event');
        }
    };

    const handleDeleteEvent = async (eventId) => {
        try {
            await axios.delete(`http://localhost:5000/events/${eventId}`, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setEvents(events.filter((event) => event._id !== eventId));
        } catch (err) {
            setError('Failed to delete event');
        }
    };

    return (
        <div className="event-form-container">
            <h2 className="form-title">Event Management</h2>
            
            <div className="event-form">
                <h3>Add Event</h3>
                <form onSubmit={handleAddEvent}>
                    <div className="form-group">
                        <label>Title:</label>
                        <input
                            type="text"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>Start Date:</label>
                        <input
                            type="date"
                            value={start}
                            onChange={(e) => setStart(e.target.value)}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>End Date:</label>
                        <input
                            type="date"
                            value={end}
                            onChange={(e) => setEnd(e.target.value)}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>Location:</label>
                        <input
                            type="text"
                            value={location}
                            onChange={(e) => setLocation(e.target.value)}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>Description:</label>
                        <textarea
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            className="form-control"
                        />
                    </div>
                    {error && <p className="error-message">{error}</p>}
                    <button type="submit" className="btn btn-primary">Add Event</button>
                </form>
            </div>

            <div className="event-list">
                <h3>All Events</h3>
                <div className="event-list-container">
                    <ul>
                        {events.map((event) => (
                            <li key={event._id} className="event-item">
                                <div>
                                    <strong>{event.title}</strong> - {event.start} to {event.end}
                                </div>
                                <button onClick={() => handleDeleteEvent(event._id)} className="btn btn-danger">
                                    Delete
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default EventForm;
