import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import CustomNavbar from './components/CustomNavbar';
import Carousel from './components/Carousel';
import Footer from './components/Footer';
import ContactUs from './pages/ContactUs';
import MyEvents from './pages/MyEvents';
import PastEvents from './pages/PastEvents';
import EventForm from './components/EventForm';
import AdminLogin from './components/AdminLogin';
import './App.css';

function App() {
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);

  // Check if the admin is logged in based on the token
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAdminLoggedIn(true);
    }
  }, []);

  const handleAdminLoginSuccess = () => {
    setIsAdminLoggedIn(true);
  };

  // Layout component for pages with Navbar and Footer
  const MainLayout = ({ children }) => (
    <>
      <CustomNavbar />
      <Carousel />
      <div className="container mt-4">{children}</div>
      <Footer />
    </>
  );

  return (
    <Router>
      <Routes>
        <Route path="/" element={<MainLayout><ContactUs /></MainLayout>} />
        <Route path="/MyEvents" element={<MainLayout><MyEvents /></MainLayout>} />
        <Route path="/PastEvents" element={<MainLayout><PastEvents /></MainLayout>} />

        {/* Admin login route */}
        <Route path="/admin/login" element={<AdminLogin onLoginSuccess={handleAdminLoginSuccess} />} />

        {/* Admin event management route - only event form without main layout */}
        {isAdminLoggedIn && (
          <Route path="/admin/events" element={<EventForm />} />
        )}
      </Routes>
    </Router>
  );
}

export default App;
