import React, { useState } from 'react';
import { Button, Navbar, Nav, Form, FormControl } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { FaBars } from 'react-icons/fa';  // Importing FontAwesome Hamburger Icon
import Login from './Login';
import Signup from './Signup';
import AdminLogin from './AdminLogin'; // Import AdminLogin component

const CustomNavbar = () => {
    const [showLogin, setShowLogin] = useState(false);
    const [showSignup, setShowSignup] = useState(false);
    const [showAdminLogin, setShowAdminLogin] = useState(false); // State for AdminLogin modal

    const handleLoginClose = () => setShowLogin(false);
    const handleSignupClose = () => setShowSignup(false);
    const handleAdminLoginClose = () => setShowAdminLogin(false); // Close admin modal

    const handleContactClick = () => {
        const element = document.getElementById('contact-details');
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        <Navbar className="navbar" expand="lg">
            {/* Hamburger Icon on the Left */}
            <Navbar.Brand as={Link} to="/" className="d-flex align-items-center">
                <FaBars /> {/* Hamburger Icon */}
            </Navbar.Brand>

            {/* Centered Website Name */}
            <Navbar.Text className="mx-auto font-weight-bold">
                <Link to="/" className="navbar-brand">The Event Solution</Link>
            </Navbar.Text>

            {/* Search Bar on the Right */}
            <Form className="ml-auto d-flex">
                <FormControl
                    type="search"
                    placeholder="Search Events"
                    className="mr-2"
                    aria-label="Search"
                />
                <Button variant="outline-success">Search</Button>
            </Form>

            <Navbar.Toggle aria-controls="dashboard-menu" />

            <Navbar.Collapse id="dashboard-menu">
                <Nav className="ml-auto">
                    <Button className="btn btn-primary mr-2" onClick={() => setShowLogin(true)}>Login</Button>
                    <Button className="btn btn-success mr-2" onClick={() => setShowSignup(true)}>Sign Up</Button>
                </Nav>
            </Navbar.Collapse>

            {/* Modals for Login, Signup, and AdminLogin */}
            <Login show={showLogin} handleClose={handleLoginClose} onLoginSuccess={() => alert('Logged in successfully')} />
            <Signup show={showSignup} handleClose={handleSignupClose} />
            <AdminLogin show={showAdminLogin} handleClose={handleAdminLoginClose} onLoginSuccess={() => alert('Admin logged in successfully')} />
        </Navbar>
    );
};

export default CustomNavbar;
