import React, { useState, useRef } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';

const CalendarSidebar = ({ events }) => {
    const calendarRef = useRef(null);
    const [selectedDate, setSelectedDate] = useState(null);
    const [filteredEvents, setFilteredEvents] = useState([]);

    // Handle 'Today' button click
    const handleToday = () => {
        let calendarApi = calendarRef.current?.getApi();
        if (calendarApi) {
            calendarApi.today();
        }
    };

    // Handle date change from input field
    const handleDateChange = (e) => {
        let calendarApi = calendarRef.current?.getApi();
        if (calendarApi) {
            calendarApi.gotoDate(e.target.value);
        }
    };

    // Handle date click from calendar
    const handleDateClick = (info) => {
        const clickedDate = info.dateStr; // The clicked date in "YYYY-MM-DD" format
        setSelectedDate(clickedDate);

        // Filter events for the clicked date
        const matchingEvents = events.filter(event => event.start.startsWith(clickedDate));
        setFilteredEvents(matchingEvents);
    };

    return (
        <div>
            <h4>Calendar</h4>
            <div className="calendar-controls mb-3">
                <button className="btn btn-secondary" onClick={handleToday}>Today</button>
                <input type="date" className="form-control" onChange={handleDateChange} />
            </div>
            <FullCalendar
                ref={calendarRef}
                plugins={[dayGridPlugin]}
                initialView="dayGridMonth"
                events={events}
                dateClick={handleDateClick} // Attach the dateClick event handler
            />

            {/* Event Details Section */}
            <div className="event-details mt-4">
                {selectedDate && (
                    <>
                        <h5>Events on {selectedDate}</h5>
                        {filteredEvents.length > 0 ? (
                            <ul>
                                {filteredEvents.map((event, index) => (
                                    <li key={index}>
                                        <strong>{event.title}</strong> - {event.start}
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>No events on this day.</p>
                        )}
                    </>
                )}
            </div>
        </div>
    );
};

export default CalendarSidebar;
