import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import CustomNavbar from './components/CustomNavbar';
import CalendarSidebar from './components/CalendarSidebar';
import EventDetails from './components/EventDetails';
import Carousel from './components/Carousel';
import Footer from './components/Footer';
import ContactUs from './pages/ContactUs';
import MyEvents from './pages/MyEvents';
import PastEvents from './pages/PastEvents';
import EventForm from './components/EventForm';  // Add EventForm for admins
import AdminLogin from './components/AdminLogin';  // Add AdminLogin component
import ProtectedRoute from './components/ProtectedRoute'; // Add ProtectedRoute component
import './App.css';

function App() {
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
    const [events, setEvents] = useState([]);
    const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);

    // Fetch events from backend
    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await fetch('http://localhost:5000/events');
                if (response.ok) {
                    const data = await response.json();
                    setEvents(data);
                } else {
                    console.error('Failed to fetch events:', response.statusText);
                }
            } catch (error) {
                console.error('Error fetching events:', error);
            }
        };

        fetchEvents();
    }, []); // Empty dependency array ensures this runs once on component mount

    // Callback for updating selected date
    const handleDateSelect = (date) => {
        setSelectedDate(date);
    };

    const handleAdminLoginSuccess = () => {
        setIsAdminLoggedIn(true);
    };

    return (
        <Router>
            <div className="App">
                <CustomNavbar />
                <Carousel />

                <div className="container mt-4">
                    <div className="row">
                        <div className="col-md-8 mb-4">
                            <EventDetails 
                                events={events} 
                                selectedDate={selectedDate} 
                                onDateChange={handleDateSelect} 
                            />
                        </div>
                        <div className="col-md-4">
                            <CalendarSidebar 
                                events={events} 
                                onDateSelect={handleDateSelect} 
                            />
                        </div>
                    </div>

                    <Routes>
                        <Route path="/MyEvents" element={<MyEvents />} />
                        <Route path="/PastEvents" element={<PastEvents />} />
                        <Route path="/contact" element={<ContactUs />} />

                        {/* Admin event management route - protected */}
                        <Route 
                            path="/admin/events" 
                            element={
                                <ProtectedRoute isAdminLoggedIn={isAdminLoggedIn}>
                                    <EventForm />
                                </ProtectedRoute>
                            }
                        />

                        {/* Admin login route */}
                        <Route 
                            path="/admin/login" 
                            element={<AdminLogin onLoginSuccess={handleAdminLoginSuccess} />} 
                        />
                    </Routes>

                    <section id="contact-details">
                        <ContactUs />
                    </section>
                </div>
                <Footer />
            </div>
        </Router>
    );
}

export default App;
