import React, { useState } from 'react';
import { Button, Navbar, Nav, NavDropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Login from './Login';
import Signup from './Signup';
import AdminLogin from './AdminLogin'; // Import AdminLogin component
import './CustomNavbar.css'; // Add your custom styles here

const CustomNavbar = () => {
    const [showLogin, setShowLogin] = useState(false);
    const [showSignup, setShowSignup] = useState(false);
    const [showAdminLogin, setShowAdminLogin] = useState(false); // State for AdminLogin modal

    const handleLoginClose = () => setShowLogin(false);
    const handleSignupClose = () => setShowSignup(false);
    const handleAdminLoginClose = () => setShowAdminLogin(false); // Close admin modal

    const handleContactClick = () => {
        const element = document.getElementById('contact-details');
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        <Navbar className="navbar navbar-expand-lg navbar-light bg-light custom-navbar">
            <Navbar.Brand as={Link} to="/" className="navbar-brand-custom">Event Solution</Navbar.Brand>

            <Navbar.Toggle aria-controls="dashboard-menu" />
            <Navbar.Collapse id="dashboard-menu">
                <Nav className="mr-auto">
                    <NavDropdown title="Dashboard" id="navbarDropdown" className="dropdown-custom">
                        <NavDropdown.Item as={Link} to="#">Home</NavDropdown.Item>
                        <NavDropdown.Item as={Link} to="#">My Events</NavDropdown.Item>
                        <NavDropdown.Item as={Link} to="#">Past Events</NavDropdown.Item>
                        <NavDropdown.Item onClick={handleContactClick}>Contact Us</NavDropdown.Item>
                        {/* Admin Login inside Dashboard */}
                        <NavDropdown.Item onClick={() => setShowAdminLogin(true)}>Admin Login</NavDropdown.Item>
                    </NavDropdown>
                </Nav>

                <Nav className="ml-auto">
                    <Button variant="outline-primary" className="mr-2" onClick={() => setShowLogin(true)}>Login</Button>
                    <Button variant="outline-success" className="mr-2" onClick={() => setShowSignup(true)}>Sign Up</Button>
                </Nav>
            </Navbar.Collapse>

            {/* Modals for Login, Signup, and AdminLogin */}
            <Login show={showLogin} handleClose={handleLoginClose} onLoginSuccess={() => alert('Logged in successfully')} />
            <Signup show={showSignup} handleClose={handleSignupClose} />
            <AdminLogin show={showAdminLogin} handleClose={handleAdminLoginClose} onLoginSuccess={() => alert('Admin logged in successfully')} />
        </Navbar>
    );
};

export default CustomNavbar;
