import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [mobile, setMobile] = useState('');
    const [email, setEmail] = useState('');
    const [experience, setExperience] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [passwordStrength, setPasswordStrength] = useState('');

    const validatePassword = (password) => {
        const passwordValid = /[A-Z]/.test(password) &&
                              /[a-z]/.test(password) &&
                              /\d/.test(password) &&
                              /[!@#$%^&*]/.test(password) &&
                              password.length >= 8;
        return passwordValid ? '' : 'Password must be at least 8 characters and contain a combination of uppercase, lowercase, number, and special character.';
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError('');
        setPasswordStrength('');

        if (age < 18 || age > 100) {
            setError('Age must be between 18 and 100.');
            return;
        }

        const mobilePattern = /^[0-9]{10}$/;
        if (!mobilePattern.test(mobile)) {
            setError('Please enter a valid 10-digit mobile number.');
            return;
        }

        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailPattern.test(email)) {
            setError('Please enter a valid email address.');
            return;
        }

        const passwordError = validatePassword(password);
        if (passwordError) {
            setPasswordStrength(passwordError);
            return;
        }

        try {
            const response = await axios.post('/api/signup', { name, age, mobile, email, experience, password });
            if (response.data.success) {
                alert('Signup successful');
            } else {
                setError(response.data.message || 'Signup failed.');
            }
        } catch (err) {
            setError('An error occurred. Please try again.');
        }
    };

    return (
        <div className="modal fade" id="signupModal" tabIndex="-1" role="dialog" aria-labelledby="signupModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title" id="signupModalLabel">Sign Up</h5>
                        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div className="modal-body">
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label htmlFor="signupName">Name</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="signupName"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    placeholder="Enter name"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="signupAge">Age</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    id="signupAge"
                                    value={age}
                                    onChange={(e) => setAge(e.target.value)}
                                    placeholder="Enter age"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="signupMobile">Mobile no.</label>
                                <input
                                    type="tel"
                                    className="form-control"
                                    id="signupMobile"
                                    value={mobile}
                                    onChange={(e) => setMobile(e.target.value)}
                                    placeholder="Whatsapp Preferable"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="signupEmail">Email address</label>
                                <input
                                    type="email"
                                    className="form-control"
                                    id="signupEmail"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    placeholder="Enter email"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="signupExperience">Experiences</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="signupExperience"
                                    value={experience}
                                    onChange={(e) => setExperience(e.target.value)}
                                    placeholder="Write event type"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="signupPassword">Password</label>
                                <input
                                    type="password"
                                    className="form-control"
                                    id="signupPassword"
                                    value={password}
                                    onChange={(e) => {
                                        setPassword(e.target.value);
                                        setPasswordStrength(validatePassword(e.target.value));
                                    }}
                                    placeholder="Password"
                                    required
                                />
                                {passwordStrength && <div className="form-text text-danger">{passwordStrength}</div>}
                            </div>
                            {error && <div className="alert alert-danger">{error}</div>}
                            <button type="submit" className="btn btn-custom btn-signup">Sign Up</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Signup;
