import React, { useState, useRef } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';

const CalendarSidebar = ({ events, onDateSelect }) => {
    const calendarRef = useRef(null);

    const handleToday = () => {
        let calendarApi = calendarRef.current?.getApi();
        if (calendarApi) {
            calendarApi.today();
        }
    };

    const handleDateChange = (e) => {
        let calendarApi = calendarRef.current?.getApi();
        if (calendarApi) {
            calendarApi.gotoDate(e.target.value);
        }
    };

    const handleDateClick = (info) => {
        const clickedDate = info.dateStr;
        onDateSelect(clickedDate);
    };

    return (
        <div>
            <h4>Calendar</h4>
            <div className="calendar-controls mb-3">
                <button className="btn btn-secondary" onClick={handleToday}>Today</button>
                <input type="date" className="form-control" onChange={handleDateChange} />
            </div>
            <FullCalendar
                ref={calendarRef}
                plugins={[dayGridPlugin, interactionPlugin]}
                initialView="dayGridMonth"
                events={events}
                dateClick={handleDateClick}  // Send clicked date to parent
            />
        </div>
    );
};

export default CalendarSidebar;
