import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './components/Navbar';
import EventDetails from './components/EventDetails';
import CalendarSidebar from './components/CalendarSidebar';
import Carousel from './components/Carousel';
import Footer from './components/Footer';
import events from './data/events';
import './App.css';
import Login from './components/Login';
import Signup from './components/Signup';
import FullCalendar from '@fullcalendar/react'; // Ensure this is only imported once
import dayGridPlugin from '@fullcalendar/daygrid';

function App() {
    const handleLoginSuccess = () => {
        alert('Login successful');
        // Implement further actions upon successful login
    };

    return (
        <div className="App">
            <Navbar />
            <div className="container mt-4">
                <div className="row">
                    <div className="col-md-8 mb-4">
                        <EventDetails events={events} />
                    </div>
                    <div className="col-md-4">
                        <CalendarSidebar events={events} />
                    </div>
                </div>
                <Carousel />
            </div>
            <Footer />

            {/* Buttons to open modals */}
            <div className="text-center mb-4">
                <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#loginModal">
                    Login
                </button>
                <button type="button" className="btn btn-secondary ml-2" data-toggle="modal" data-target="#signupModal">
                    Sign Up
                </button>
            </div>

            {/* Modal Components */}
            <Login onLoginSuccess={handleLoginSuccess} />
            <Signup />
        </div>
    );
}

export default App;
