import React, { useState } from 'react';
import { Button, Navbar, Nav, Offcanvas, Form, FormControl } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { FaBars } from 'react-icons/fa';
import Login from './Login';
import Signup from './Signup';
import AdminLogin from './AdminLogin'; 

const CustomNavbar = ({ events }) => {
    const [showOffcanvas, setShowOffcanvas] = useState(false);  
    const [showLogin, setShowLogin] = useState(false);
    const [showSignup, setShowSignup] = useState(false);
    const [showAdminLogin, setShowAdminLogin] = useState(false); 
    const [searchQuery, setSearchQuery] = useState(''); // State to hold search input
    const [filteredEvents, setFilteredEvents] = useState([]); // State to hold filtered events

    const handleLoginClose = () => setShowLogin(false);
    const handleSignupClose = () => setShowSignup(false);
    const handleAdminLoginClose = () => setShowAdminLogin(false);

    const handleOffcanvasClose = () => setShowOffcanvas(false);
    const handleOffcanvasShow = () => setShowOffcanvas(true);  

    const handleContactClick = () => {
        const element = document.getElementById('contact-details');
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    // Function to handle search input change
    const handleSearchChange = (e) => {
        const query = e.target.value.toLowerCase();
        setSearchQuery(query);
    
        // Filter events based on search query
        if (query) {
            const filtered = events.filter(event => {
                const title = event.title ? event.title.toLowerCase() : '';  // Ensure title is defined
                const description = event.description ? event.description.toLowerCase() : '';  // Ensure description is defined
                return title.includes(query) || description.includes(query);
            });
            setFilteredEvents(filtered);
        } else {
            setFilteredEvents([]); // Reset if query is empty
        }
    };
    
    return (
        <>
            <Navbar className="navbar" expand="lg" style={{ backgroundColor: '#ccd1d1', position: 'sticky' }}>
                {/* Hamburger Icon on the Left */}
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <FaBars onClick={handleOffcanvasShow} style={{ cursor: 'pointer', color: '#050404', fontSize: '24px' }} />
                </div>

                {/* Centered Website Name */}
                <Navbar.Brand className="mx-auto" style={{ textAlign: 'center', color: '#fff', fontWeight: 'bold', position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}>
                    <Link to="/" style={{ color: '#050404', textDecoration: 'none' }}>ùúÆùùÇùú∫ùúºùùâ ùë∫ùúΩùúæùùÅùùâùíäùúΩùúº</Link>
                </Navbar.Brand>

                {/* Search Bar on the Right */}
                <div style={{ marginLeft: 'auto', display: 'flex', alignItems: 'center', position: 'relative' }}>
                    <Form className="d-flex" style={{ marginRight: '16px' }}>
                        <FormControl
                            type="search"
                            placeholder="Search Events"
                            className="mr-2"
                            aria-label="Search"
                            value={searchQuery}
                            onChange={handleSearchChange} // Update search query
                        />
                        <Button variant="outline-dark">Search</Button>
                    </Form>

                    {/* Display filtered event options */}
                    {filteredEvents.length > 0 && (
                        <ul className="list-group" style={{ position: 'absolute', top: '100%', left: 0, zIndex: 1000, width: '100%' }}>
                            {filteredEvents.map((event, index) => (
                                <li key={index} className="list-group-item">
                                    <Link to={`/events/${event.id}`} style={{ textDecoration: 'none', color: 'black' }}>
                                        {event.title}
                                    </Link>
                                </li>
                            ))}
                        </ul>
                    )}

                    {/* Login and Signup buttons */}
                    <Nav>
                        <Button className="btn btn-primary mr-2" onClick={() => setShowLogin(true)}>Login</Button>
                        <Button className="btn btn-success" onClick={() => setShowSignup(true)}>Sign Up</Button>
                    </Nav>
                </div>
            </Navbar>

            {/* Offcanvas (Hamburger) Menu */}
            <Offcanvas show={showOffcanvas} onHide={handleOffcanvasClose} placement="start">
                <Offcanvas.Header closeButton>
                    <Offcanvas.Title>Menu</Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>
                    <Nav className="flex-column">
                        <Nav.Link as={Link} to="/">Home</Nav.Link>
                        <Nav.Link as={Link} to="/MyEvents">My Events</Nav.Link>
                        <Nav.Link as={Link} to="/PastEvents">Past Events</Nav.Link>
                        <Nav.Link onClick={handleContactClick}>Contact Us</Nav.Link>
                        <Nav.Link onClick={() => setShowAdminLogin(true)}>Admin Login</Nav.Link>
                    </Nav>
                </Offcanvas.Body>
            </Offcanvas>

            {/* Modals for Login, Signup, and AdminLogin */}
            <Login show={showLogin} handleClose={handleLoginClose} onLoginSuccess={() => alert('Logged in successfully')} />
            <Signup show={showSignup} handleClose={handleSignupClose} />
            <AdminLogin show={showAdminLogin} handleClose={handleAdminLoginClose} onLoginSuccess={() => alert('Admin logged in successfully')} />
        </>
    );
};

export default CustomNavbar;
