import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import Login from './Login';
import Signup from './Signup';
import 'bootstrap/dist/css/bootstrap.min.css';

const Navbar = () => {
    const [showLogin, setShowLogin] = useState(false);
    const [showSignup, setShowSignup] = useState(false);

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <a className="navbar-brand" href="#">The Event Solution</a>
            <div className="collapse navbar-collapse">
                <ul className="navbar-nav ml-auto">
                    <li className="nav-item">
                        <Button className="btn btn-primary" onClick={() => setShowLogin(true)}>Login</Button>
                    </li>
                    <li className="nav-item">
                        <Button className="btn btn-success ml-2" onClick={() => setShowSignup(true)}>Sign Up</Button>
                    </li>
                </ul>
            </div>

            {/* Login Modal */}
            <Modal show={showLogin} onHide={() => setShowLogin(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Login</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Login />
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowLogin(false)}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>

            {/* Sign Up Modal */}
            <Modal show={showSignup} onHide={() => setShowSignup(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Sign Up</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Signup />
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowSignup(false)}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </nav>
    );
};

export default Navbar;
