import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form, Row, Col, Table } from 'react-bootstrap';

const EventForm = ({ show, handleClose }) => {
    const [title, setTitle] = useState('');
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [location, setLocation] = useState('');
    const [description, setDescription] = useState('');
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    useEffect(() => {
        // Fetch all events when the page loads
        const fetchEvents = async () => {
            try {
                const response = await axios.get('http://localhost:5000/events');
                setEvents(response.data);
            } catch (error) {
                console.error('Failed to fetch events:', error);
            }
        };
        fetchEvents();
    }, []);

    const handleAddEvent = async (e) => {
        e.preventDefault();

        if (!title || !start || !end || !location || !description) {
            setError('Please fill all fields');
            return;
        }

        try {
            const newEvent = { title, start, end, extendedProps: { location, description } };
            await axios.post('http://localhost:5000/events', newEvent, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setEvents([...events, newEvent]);
            setTitle('');
            setStart('');
            setEnd('');
            setLocation('');
            setDescription('');
            setError('');
            setSuccess('Event added successfully');
        } catch (err) {
            setError('Failed to add event');
            setSuccess('');
        }
    };

    const handleDeleteEvent = async (eventId) => {
        try {
            await axios.delete(`http://localhost:5000/events/${eventId}`, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setEvents(events.filter((event) => event._id !== eventId));
            setSuccess('Event deleted successfully');
            setError('');
        } catch (err) {
            setError('Failed to delete event');
            setSuccess('');
        }
    };

    return (
        <Modal show={show} onHide={handleClose} size="lg" centered>
            <Modal.Header closeButton>
                <Modal.Title>Event Management</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Row>
                    {/* Add Event Section */}
                    <Col md={6}>
                        <h4>Add Event</h4>
                        <Form onSubmit={handleAddEvent}>
                            <Form.Group controlId="formTitle" className="mb-3">
                                <Form.Label>Title</Form.Label>
                                <Form.Control
                                    type="text"
                                    value={title}
                                    onChange={(e) => setTitle(e.target.value)}
                                    placeholder="Event title"
                                />
                            </Form.Group>

                            <Form.Group controlId="formStart" className="mb-3">
                                <Form.Label>Start Date</Form.Label>
                                <Form.Control
                                    type="date"
                                    value={start}
                                    onChange={(e) => setStart(e.target.value)}
                                />
                            </Form.Group>

                            <Form.Group controlId="formEnd" className="mb-3">
                                <Form.Label>End Date</Form.Label>
                                <Form.Control
                                    type="date"
                                    value={end}
                                    onChange={(e) => setEnd(e.target.value)}
                                />
                            </Form.Group>

                            <Form.Group controlId="formLocation" className="mb-3">
                                <Form.Label>Location</Form.Label>
                                <Form.Control
                                    type="text"
                                    value={location}
                                    onChange={(e) => setLocation(e.target.value)}
                                    placeholder="Event location"
                                />
                            </Form.Group>

                            <Form.Group controlId="formDescription" className="mb-3">
                                <Form.Label>Description</Form.Label>
                                <Form.Control
                                    as="textarea"
                                    rows={3}
                                    value={description}
                                    onChange={(e) => setDescription(e.target.value)}
                                    placeholder="Event description"
                                />
                            </Form.Group>

                            {error && <p className="text-danger">{error}</p>}
                            {success && <p className="text-success">{success}</p>}
                            <Button type="submit" variant="primary" className="w-100">Add Event</Button>
                        </Form>
                    </Col>

                    {/* Manage (Delete) Events Section */}
                    <Col md={6}>
                        <h4>Manage Events</h4>
                        <Table bordered hover size="sm" responsive>
                            <thead>
                                <tr>
                                    <th>Title</th>
                                    <th>Date</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {events.map((event) => (
                                    <tr key={event._id}>
                                        <td>{event.title}</td>
                                        <td>{event.start} - {event.end}</td>
                                        <td>
                                            <Button 
                                                variant="danger" 
                                                size="sm" 
                                                onClick={() => handleDeleteEvent(event._id)}
                                            >
                                                Delete
                                            </Button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    </Col>
                </Row>
            </Modal.Body>
        </Modal>
    );
};

export default EventForm;
