import React, { useState } from 'react';
import { Button, Collapse } from 'react-bootstrap';
import Login from './Login';
import Signup from './Signup';
import 'bootstrap/dist/css/bootstrap.min.css'; // Make sure Bootstrap CSS is imported

const Navbar = () => {
    const [showLogin, setShowLogin] = useState(false);
    const [showSignup, setShowSignup] = useState(false);
    const [isNavOpen, setIsNavOpen] = useState(false);

    const handleLoginClose = () => setShowLogin(false);
    const handleSignupClose = () => setShowSignup(false);
    const toggleNav = () => setIsNavOpen(!isNavOpen);

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <a className="navbar-brand" href="#">The Event Solution</a>
            <button className="navbar-toggler" type="button" onClick={toggleNav} aria-controls="navbarNav" aria-expanded={isNavOpen} aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <Collapse in={isNavOpen}>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item">
                            <Button className="btn btn-primary" onClick={() => setShowLogin(true)}>Login</Button>
                        </li>
                        <li className="nav-item">
                            <Button className="btn btn-success ml-2" onClick={() => setShowSignup(true)}>Sign Up</Button>
                        </li>
                    </ul>
                </div>
            </Collapse>

            <Login show={showLogin} handleClose={handleLoginClose} onLoginSuccess={() => alert('Logged in successfully')} />
            <Signup show={showSignup} handleClose={handleSignupClose} />
        </nav>
    );
};

export default Navbar;
