import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button } from 'react-bootstrap';

const EventForm = ({ show, handleClose }) => {
    const [title, setTitle] = useState('');
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [location, setLocation] = useState('');
    const [description, setDescription] = useState('');
    const [events, setEvents] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        // Fetch all events when the page loads
        const fetchEvents = async () => {
            try {
                const response = await axios.get('http://localhost:5000/events');
                setEvents(response.data);
            } catch (error) {
                console.error('Failed to fetch events:', error);
            }
        };
        fetchEvents();
    }, []);

    const handleAddEvent = async (e) => {
        e.preventDefault();

        if (!title || !start || !end || !location || !description) {
            setError('Please fill all fields');
            return;
        }

        try {
            const newEvent = { title, start, end, extendedProps: { location, description } };
            await axios.post('http://localhost:5000/events', newEvent, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setEvents([...events, newEvent]);
            setTitle('');
            setStart('');
            setEnd('');
            setLocation('');
            setDescription('');
        } catch (err) {
            setError('Failed to add event');
        }
    };

    const handleDeleteEvent = async (eventId) => {
        try {
            await axios.delete(`http://localhost:5000/events/${eventId}`, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setEvents(events.filter((event) => event._id !== eventId));
        } catch (err) {
            setError('Failed to delete event');
        }
    };

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Event Management</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <form onSubmit={handleAddEvent}>
                    <div>
                        <label>Title:</label>
                        <input
                            type="text"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                        />
                    </div>
                    <div>
                        <label>Start Date:</label>
                        <input
                            type="date"
                            value={start}
                            onChange={(e) => setStart(e.target.value)}
                        />
                    </div>
                    <div>
                        <label>End Date:</label>
                        <input
                            type="date"
                            value={end}
                            onChange={(e) => setEnd(e.target.value)}
                        />
                    </div>
                    <div>
                        <label>Location:</label>
                        <input
                            type="text"
                            value={location}
                            onChange={(e) => setLocation(e.target.value)}
                        />
                    </div>
                    <div>
                        <label>Description:</label>
                        <textarea
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                    </div>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <Button type="submit">Add Event</Button>
                </form>

                <h3>All Events</h3>
                <ul>
                    {events.map((event) => (
                        <li key={event._id}>
                            {event.title} - {event.start} to {event.end}
                            <Button onClick={() => handleDeleteEvent(event._id)}>Delete</Button>
                        </li>
                    ))}
                </ul>
            </Modal.Body>
        </Modal>
    );
};

export default EventForm;
