import React, { useState } from 'react';
import { Button, Navbar, Nav, NavDropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Login from './Login';
import Signup from './Signup';

const CustomNavbar = () => {
    const [showLogin, setShowLogin] = useState(false);
    const [showSignup, setShowSignup] = useState(false);

    const handleLoginClose = () => setShowLogin(false);
    const handleSignupClose = () => setShowSignup(false);

    const handleContactClick = () => {
        const element = document.getElementById('contact-details');
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        <Navbar  className="navbar">
            <Navbar.Toggle aria-controls="dashboard-menu" className="mr-2" />
            <Navbar.Brand as={Link} to="/">The boss</Navbar.Brand>

            <Navbar.Collapse id="dashboard-menu">
                <Nav className="mr-auto">
                    <NavDropdown title="Dashboard" id="navbarDropdown">
                        <NavDropdown.Item as={Link} to="#">Home</NavDropdown.Item>
                        <NavDropdown.Item as={Link} to="#">My Events</NavDropdown.Item>
                        <NavDropdown.Item as={Link} to="#">Past Events</NavDropdown.Item>
                        <NavDropdown.Item onClick={handleContactClick}>Contact Us</NavDropdown.Item> {/* Updated here */}
                    </NavDropdown>
                </Nav>

                <Nav className="ml-auto">
                    <Button className="btn btn-primary mr-2" onClick={() => setShowLogin(true)}>Login</Button>
                    <Button className="btn btn-success" onClick={() => setShowSignup(true)}>Sign Up</Button>
                </Nav>
            </Navbar.Collapse>

            <Login show={showLogin} handleClose={handleLoginClose} onLoginSuccess={() => alert('Logged in successfully')} />
            <Signup show={showSignup} handleClose={handleSignupClose} />
        </Navbar>
    );
};

export default CustomNavbar;
