import React, { useState, useRef } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';

const CalendarSidebar = ({ events, onDateSelect }) => {
    const calendarRef = useRef(null);
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);

    const handleToday = () => {
        let calendarApi = calendarRef.current?.getApi();
        if (calendarApi) {
            calendarApi.today();
            const today = new Date().toISOString().split('T')[0];
            setSelectedDate(today);
            onDateSelect(today);
        }
    };

    const handlePrev = () => {
        const prevDate = new Date(selectedDate);
        prevDate.setDate(prevDate.getDate() - 1);
        setSelectedDate(prevDate.toISOString().split('T')[0]);
        onDateSelect(prevDate.toISOString().split('T')[0]);
    };

    const handleNext = () => {
        const nextDate = new Date(selectedDate);
        nextDate.setDate(nextDate.getDate() + 1);
        setSelectedDate(nextDate.toISOString().split('T')[0]);
        onDateSelect(nextDate.toISOString().split('T')[0]);
    };

    const handleDateChange = (e) => {
        const selected = e.target.value;
        setSelectedDate(selected);
        onDateSelect(selected);
    };

    const handleDateClick = (info) => {
        const clickedDate = info.dateStr;
        setSelectedDate(clickedDate);
        onDateSelect(clickedDate);
    };

    return (
        <div>
            <h4>Calendar</h4>
            <div className="calendar-controls mb-3">
                <button className="btn btn-secondary" onClick={handlePrev}>Previous Day</button>
                <button className="btn btn-secondary" onClick={handleToday}>Today</button>
                <button className="btn btn-secondary" onClick={handleNext}>Next Day</button>
                <input type="date" className="form-control mt-2" value={selectedDate} onChange={handleDateChange} />
            </div>
            <FullCalendar
                ref={calendarRef}
                plugins={[dayGridPlugin, interactionPlugin]}
                initialView="dayGridMonth"
                events={events}
                dateClick={handleDateClick}
            />
        </div>
    );
};

export default CalendarSidebar;
