import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import CustomNavbar from './components/CustomNavbar';
import EventDetails from './components/EventDetails';
import CalendarSidebar from './components/CalendarSidebar';
import Carousel from './components/Carousel';
import Footer from './components/Footer';
import ContactUs from './pages/ContactUs';
import HomePage from './pages/HomePage'; // Assuming this exists
import MyEvents from './pages/MyEvents'; // Assuming this exists
import PastEvents from './pages/PastEvents'; // Assuming this exists
import events from './data/events';
import './App.css';
import '@fullcalendar/common/main.css'; // Core styles
import '@fullcalendar/daygrid/main.css'; // Styles specific to the day grid view

function App() {
    const [selectedEvent, setSelectedEvent] = useState(null);

    const handleEventClick = (clickInfo) => {
        setSelectedEvent(clickInfo.event);
    };

    return (
        <Router>
            <div className="App">
                {/* Navbar */}
                <CustomNavbar />

                {/* Main Content */}
                <div className="container mt-4">
                    <div className="row">
                        <div className="col-md-8 mb-4">
                            <EventDetails event={selectedEvent} />
                        </div>
                        <div className="col-md-4">
                            <CalendarSidebar events={events} onEventClick={handleEventClick} />
                        </div>
                    </div>

                    {/* Routes for different pages */}
                    <Routes>
                        <Route path="/" element={<HomePage />} />
                        <Route path="/MyEvents" element={<MyEvents />} />
                        <Route path="/PastEvents" element={<PastEvents />} />
                        <Route path="/contact" element={<ContactUs />} />
                    </Routes>

                    {/* Carousel */}
                    <Carousel />
                </div>

                {/* Footer */}
                <Footer />
            </div>
        </Router>
    );
}

export default App;
