import React, { useState, useRef } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import EventDetails from './EventDetails';  // Import EventDetails

const CalendarSidebar = ({ events }) => {
    const calendarRef = useRef(null);
    const [ setSelectedDate] = useState(null);
    const [filteredEvents, setFilteredEvents] = useState([]);

    const handleToday = () => {
        let calendarApi = calendarRef.current?.getApi();
        if (calendarApi) {
            calendarApi.today();
        }
    };

    const handleDateChange = (e) => {
        let calendarApi = calendarRef.current?.getApi();
        if (calendarApi) {
            calendarApi.gotoDate(e.target.value);
        }
    };

    const handleDateClick = (info) => {
        const clickedDate = info.dateStr;
        setSelectedDate(clickedDate);

        const matchingEvents = events.filter(event => {
            const eventDate = event.start.split('T')[0];
            return eventDate === clickedDate;
        });
        setFilteredEvents(matchingEvents);
    };

    return (
        <div>
            <h4>Calendar</h4>
            <div className="calendar-controls mb-3">
                <button className="btn btn-secondary" onClick={handleToday}>Today</button>
                <input type="date" className="form-control" onChange={handleDateChange} />
            </div>
            <FullCalendar
                ref={calendarRef}
                plugins={[dayGridPlugin, interactionPlugin]}
                initialView="dayGridMonth"
                events={events}
                dateClick={handleDateClick}
            />

            {/* Pass filtered events to the EventDetails component */}
            <div className="event-details-section mt-4">
                {filteredEvents.length > 0 ? (
                    <EventDetails events={filteredEvents} />
                ) : (
                    <p>No events for this date.</p>
                )}
            </div>
        </div>
    );
};

export default CalendarSidebar;
