// src/components/NavigationBar.js
import React from 'react';
import { Navbar, Nav, Form, FormControl, Button, NavDropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const NavigationBar = ({ handleShowLogin, handleShowSignup }) => {
  return (
    <Navbar bg="light" expand="lg">
      <Navbar.Brand href="#">The Event Solution</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <NavDropdown title="Menu" id="basic-nav-dropdown">
            <NavDropdown.Item href="HomePage.HTML">Home</NavDropdown.Item>
            <NavDropdown.Item href="#">My Events</NavDropdown.Item>
            <NavDropdown.Item href="#">Past Events</NavDropdown.Item>
            <NavDropdown.Item href="contact.html">Contact</NavDropdown.Item>
          </NavDropdown>
        </Nav>
        <Form inline className="mr-3">
          <FormControl type="text" placeholder="Search events" className="mr-sm-2" />
          <Button variant="outline-success">Search</Button>
        </Form>
        <Button variant="primary" onClick={handleShowLogin}>Login</Button>
        <Button variant="success" onClick={handleShowSignup} className="ml-2">Sign Up</Button>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default NavigationBar;
