import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Form } from 'react-bootstrap';

const ContactUs = () => {
    const handleSubmit = (event) => {
        event.preventDefault();
        // Display the thank you modal
        window.$('#thankYouModal').modal('show');
        // Optionally reset the form here
        event.target.reset();
    };

    return (
        <>
            {/* Contact Section */}
            <section id="contact" className="p-4 bg-light border-top">
                <h2 className="text-primary">Contact Us</h2>
                <p>If you want to volunteer for our events, please contact us or submit your details, and we will get in touch with you.</p>
                <p>We work only with genuine companies.</p>
                
                {/* Contact Form */}
                <Form id="contactForm" onSubmit={handleSubmit} style={{ maxWidth: '600px', margin: '0 auto' }}>
                    <Form.Group controlId="formName">
                        <Form.Label>Name:</Form.Label>
                        <Form.Control type="text" placeholder="Your Name" required />
                    </Form.Group>
                    
                    <Form.Group controlId="formEmail">
                        <Form.Label>Email:</Form.Label>
                        <Form.Control type="email" placeholder="Your Email" required />
                    </Form.Group>
                    
                    <Form.Group controlId="formMessage">
                        <Form.Label>Message:</Form.Label>
                        <Form.Control as="textarea" rows={4} placeholder="Your Message" required />
                    </Form.Group>
                    
                    <Button type="submit" className="btn btn-primary">Send Message</Button>
                </Form>
                
                {/* Contact Details */}
                <div className="contact-details mt-3">
                    <p>Email: <a href="mailto:anuragdubey23004@gmail.com">anuragdubey23004@gmail.com</a></p>
                    <p>Phone: +91 91528-05940</p>
                </div>
            </section>
            
            {/* Thank You Modal */}
            <Modal id="thankYouModal" tabindex="-1" role="dialog" aria-labelledby="thankYouModalLabel" aria-hidden="true">
                <Modal.Dialog>
                    <Modal.Content>
                        <Modal.Header>
                            <Modal.Title id="thankYouModalLabel">Thank You!</Modal.Title>
                            <Button variant="close" aria-label="Close" onClick={() => window.$('#thankYouModal').modal('hide')}></Button>
                        </Modal.Header>
                        <Modal.Body>
                            Thank you for your message! We will get back to you soon.
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={() => window.$('#thankYouModal').modal('hide')}>Close</Button>
                        </Modal.Footer>
                    </Modal.Content>
                </Modal.Dialog>
            </Modal>
        </>
    );
};

export default ContactUs;
