import React, { useState } from 'react';

const EventDetails = ({ events }) => {
    const [currentEvent, setCurrentEvent] = useState(0);

    const handlePrev = () => {
        setCurrentEvent(currentEvent > 0 ? currentEvent - 1 : 0);
    };

    const handleNext = () => {
        setCurrentEvent(currentEvent < events.length - 1 ? currentEvent + 1 : currentEvent);
    };

    const formatEventDetails = (event) => {
        return (
            <>
                
                <h3>{event.title}</h3>
                <p>Date Start: {new Date(event.start).toLocaleDateString()}</p>
                <p>Date End: {event.end ? new Date(event.end).toLocaleDateString() : 'N/A'}</p>
                <p>Location: {event.extendedProps?.location || 'N/A'}</p>
                <p>Description: {event.extendedProps?.description || 'N/A'}</p>
            </>
        );
    };

    return (
        <div>
            <h2>Event Details</h2>
            <div id="event-details" style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '10px', backgroundColor: '#ffffff', height: '300px', overflowY: 'auto', marginBottom: '20px' }}>
                {formatEventDetails(events[currentEvent])}
            </div>
            <button className="btn btn-primary" onClick={handlePrev}>Previous</button>
            <button className="btn btn-primary" onClick={handleNext}>Next</button>
        </div>
    );
};

export default EventDetails;
